import ConfigParser

class Configure(object):
    """
    Configure
    """
    def __init__(self, file):
        """
        init ConfigMgr
        """
        self.conf = ConfigParser.ConfigParser()
        self.conf.read(file)

    def get(self, section, key):
        """
        get
        """
        return self.conf.get(section, key)

